(43, 'نفیسه احمدی', '👩🏼‍🏫 استاد #نفیسه_احمدی\n📚 دروس #علوم_پایه\n\n✅ نکات مثبت:\n۱- تدریس عالی\n۲- جزوه کامل و قابل فهم\n۳- خوش اخلاق و گرم گرفتن با دانشجویان\n۴- عدم اهمیت حضور و غیاب (ولی در صورت عدم غیبت، در امتحان نهایی کمک می کنند) \n۵- سوالات امتحان، همانند جزوه\n\n❌ نکات منفی:\n۱- سخت گیری در نمره دادن\n\nپ.ن.۱: به خود بنده(دوستی که توضیح دادن)، ۹ رو ۱۰ ندادند ! 😂\nپ.ن.۲:دوست دیگمون گفتن نمره خوب میدن!!\n\n@WTIAU_ostad 🤓'),
(44, 'امیرحسین صالحی شایگان', '👨🏼‍🏫 استاد #امیرحسین_صالحی_شایگان\n📚 دروس #علوم_پایه\n✅ نکات مثبت:\n۱- تدریس معمولی\n۳- جزوه معمولی\n۴- حل تمرین نمره مثبت داره\n۵- عدم اهمیت حضور و غیاب\n\n❌ نکات منفی:\n۱- در جزوشون مثال حل شده به اندازه نیست\n۲- سر امتحان کمک نمیکنن\n\nپ.ن.۱: دوستمون ۸.۵  میگرفتن نمره امتحان رو ۱۰ دادن!!\n\nپ.ن.۲: یه دوستمون گفتن ایشون اصلا نمره نمیدن و حتی با اینکه ۹ نمره برگه نوشته بودن ۷.۵ شدن!!\n\nپ.ن.۳: میگن حضور اهمیت نداره اما در پایان برای اونایی ک حاضر بودن نمره قائل میشن!! \n\n@WTIAU_ostad 🤓'),
(45, 'حجت الله گودرزی', '👨🏼‍🏫 استاد #حجت_الله_گودرزی\n📚 دروس تخصصی ، رشته #برق\n✅ نکات مثبت :\n۱-جزوه کامل و قابل فهم\n۲-امتحان در سطح کلاس و بچه ها\n۳-ارفاق میکنه ولی کم\n۴-درس دادن خوب\n\n❌ نکات منفی :\n۱-مهم بودن حضور و غیاب\n۲-سرعت زیاد در تدریس\n\nپ.ن : برای استاد اهمیتی نداره که درسو یاد گرفتی یا نه. سر کلاس یا کلا باید گوش بدی یا کلا بنویسی چون سرعت درس دادنشون زیاده . و اینکه سر کلاس اذیتشون نکنین چون میندازنتون\n\n@WTIAU_ostad 🤓'),
(46, 'علی رحمانی', '👨🏼‍🏫 استاد #علی_رحمانی\n📚 دروس #معارف\n✅ نکات مثبت:\n۱- جزوه خلاصه و مختصر\n۲- امتحان عین جزوه\n۳- ارفاق مناسب\n۴- به اعتراض نمره اهمیت میدن\n\n❌ نکات منفی:\n۱- اهمیت حضور و غیاب\n\n@WTIAU_ostad 🤓'),
(47, 'سعید صیدانلو', '👨🏼‍🏫 استاد #سعید_صیدانلو\n📚 دروس #علوم_پایه\n✅ نکات مثبت :\n۱-جزوه کامل و قابل فهم\n۲-امتحان میان ترم مثل خود جزوه\n۳-ارفاق میکنه ولی زیاد نه\n۴-درس دادن خوب\n۵-به حضور غیابم زیاد اهمیت نمیده\n۶-اخلاق خوب.کلا کاری‌به کارت نداره\n\n❌ نکات منفی :\n۱-ندارد\n\nپ.ن: یه دوستمون گفتن استاد خسته ای هستن و با ۹ زیاد میندازن🙄😕\n\n@WTIAU_ostad 🤓'),
(48, 'مرتضی یوسفی', '👨🏼‍🏫 استاد #مرتضی_یوسفی\n📚 دروس تخصصی ، رشته #صنایع و #مکانیک\n✅ نکات مثبت :\n۱-جزوه کامل و قابل فهم\n۲-جزوه کم‌میگن\n۳-کلاساشون خشک‌نیس\n۴-درس دادنشونم خوبه\n\n❌ نکات منفی :\n۱-مهم بودن حضور غیاب\n۲-امتحان بالاتر از سطح جزوه\n۳-سخت تصحیح کردن و پاس کردن\n۴-هیچ ارفاقی نمیکنن.\n\n@WTIAU_ostad 🤓'),
(49, 'ونوس مرضی', '👩🏼‍🏫 استاد #ونوس_مرضی\n📚 دروس تخصصی ، رشته #کامپیوتر\n✅ نکات مثبت:\n۱- سطح علمی خوب\n\n❌ نکات منفی:\n۱- اهمیت حضور و غیاب\n۲- عدم ارفاق در نمره\n۳-امتحان سخت\n\nپ.ن: از تعداد زیادی شنیدیم که میندازن و اصلا نمره خوب نمیدن!!!!\n\n\n@WTIAU_ostad 🤓'),
(50, 'پریسا عربلو', '👩🏼‍🏫 استاد #پریسا_عربلو\n📚 دروس #علوم_پایه\n✅ نکات مثبت:\n۱- امتحان راحت\n۲- تدریس خوب\n۳- خوش اخلاق\n۴-نمره دهی خوب\n۵- عدم اهمیت حضور و غیاب\n\n❌ نکات منفی:\nندارد...!!\n\nپ.ن: یکی از دوستان گفتن ایشون سوال شفاهی میپرسن و نمره منفی میدن توقعشون بابت جواب دهی به سوالات از دانشجو بالاست و نمره دهی هم زیاد مناسب نیست\n\n@WTIAU_ostad 🤓'),
(51, 'گرشاسب خزایی', '👨🏼‍🏫 استاد #گرشاسب_خزایی\n📚 دروس تخصصی ، رشته #عمران\n✅ نکات مثبت:\nندارد...!!\n\n❌ نکات منفی:\n۱- اهمیت حضور و غیاب\n۲- تدریس ضعیف\n۳- پراکنده و ناقص درس دادن\n۴- جزوه مشخصی هم ندارن خودتون باید از حرفاشون بنویسین\n۵- نمره نمیدن\n۶- به بچه ها میگن سر امتحان پاسخگو هستن ولی درواقع پاسخی نمیدن!\n\nپ.ن: توصیه کردن تا جایی که امکان هست برندارین درسی رو باهاشون!!\n\n@WTIAU_ostad 🤓'),
(52, 'شهراد کاشانی', '👨🏼‍🏫 استاد #شهراد_کاشانی\n📚 دروس تخصصی ، رشته #کامپیوتر\n✅ نکات مثبت:\n۱- گرفتن میان ترم\n۲- تعداد بالایی از سوالات امتحانیشون عین میان ترم بوده\n۳-عدم اهمیت حضور و غیاب\n\n❌ نکات منفی:\n۱- تدریس نامناسب\n۲- ۴ تا جزوه pdf\n\nپ.ن: جدید اومدن!!\n\n@WTIAU_ostad 🤓'),
(53, 'محمدمهدی حسنی', '👨🏼‍🏫 استاد #محمدمهدی_حسنی\n📚 دروس تخصصی ، رشته #کامپیوتر\n✅ نکات مثبت:\n۱- اگه اذیتشون نکنید گیر نمیدن\n۲- ارفاق بالا در نمره\n۳-سطح علمی بالا\n\n❌ نکات منفی:\n۱- اهمیت حضور و غیاب\n۲- جزوه پراکنده\n۳- تدریس نامناسب\n۴-کلاس کسل کنندست\n\nپ.ن: در مورد این استاد یکم گیج شدیم😐نکات منفیشون این ۴ مورد نظرا ثابته ولی ی ترم انگار بد نمره میدن ی ترم خوب کلا نوسان زیادی دارن سعی کنید اذیتشون نکنید🤦‍♀\nو اینکه به هیچ عنوان سر کلاس بهشون نگید \"خسته نباشید\" و اینکه خودشون میگن اگه کسی همه جلسات رو حاضر باشه با نمره بالا پاس میشه\n\n@WTIAU_ostad 🤓'),
(54, 'مجید ایلچی', '👨🏼‍🏫 استاد #مجید_ایلچی\n📚 دروس تخصصی ، رشته #عمران\n✅ نکات مثبت:\n۱- تدریس طبق برنامه اولیه که میگن\n۲- حضور و غیاب رو میگن دوست ندارید نیاید اما اومدید ساکت باشید\n۳- تمرین رو حل کنید نمره داره\n۴- سرامتحان کمک میکنن\n۵- امتحان مثل جزوه و ی دونه از تمرین\n۶- حل تمرین زیاد برای فهماندن درس به دانشجویان\n\n❌ نکات منفی:\n۱- عدم ارفاق در نمره\n۲- حساسیت برای ارامش کلاس\n\nپ.ن: جلسه آخر رفع اشکال!\n\n@WTIAU_ostad 🤓'),
(55, 'مهدی شریفی', '👨🏼‍🏫 استاد #مهدی_شریفی\n📚 دروس #علوم_پایه\n✅ نکات مثبت:\n۱- خوش اخلاق\n۲- کنار اومدن با دانشجویان\n۳- نمره برای حضور و غیاب\n۴- دست باز صحیح میکنن امتحان رو\n۵- نمره دادن هم عالی\n\n❌ نکات منفی:\nندارد...!!\n\nپ.ن: جدید اومدن!!\n\n@WTIAU_ostad 🤓'),
(56, 'کیوان میکائیلی', '👨🏼‍🏫 استاد #کیوان_میکائیلی\n📚 دروس #علوم_پایه \n✅ نکات مثبت :\n۱- عدم اهمیت به حضور و غیاب\n۲- جزوشون بسیار جامع و خوب\n\n❌ نکات منفی :\n۱- سخت گیر در کلاس\n۲- عدم شباهت سوالات امتحان با جزوه\n۳-نمره دهی نامناسب\n\nپ.ن: اصولا میندازن!!(تعداد بالایی از دوستان گفتن این موضوع رو)\n\n@WTIAU_ostad 🤓'),
(57, 'حمیدرضا لطفعلی شمیرانی', '👨🏼‍🏫 استاد #حمیدرضا_لطفعلی_شمیرانی\n📚 دروس تخصصی ، رشته #کامپیوتر\n✅ نکات مثبت :\n۱-اخلاق خوب\n۲-تدریس سطح بالا\n۳-نمره برگه رو میده + ارفاق\n۴-جزوه کامل\n\n❌ نکات منفی :\n۱-تا آخر کلاس بچه هارو نگه میداره\n۲-آنتراک نمیدن سر کلاس\n\nپ.ن : در کل استاد خوبین فقط تمام تایم کلاس رو بچه هارو نگه میدارن\n\n@WTIAU_ostad 🤓'),
(58, 'تایماز فتح الهی خلخالی', '👨🏼‍🏫 استاد #تایماز_فتح_الهی_خلخالی\n📚 دروس #علوم_پایه #فیزیک \n✅ نکات مثبت:\n۱-اخلاق خوب \n۲-بار علمی بالا\n۳-تشابه جزوه ارائه شده با برخی از سوالات امتحان\n\n\n❌ نکات منفی:\n۱-اگر تو طول ترم نخونید میوفتید\n۲-در کل پاس میکنه ولی ترم با ترم فرق میکنه (یک بار دست بالا صحیح میکنه یک بار با 10 پاس میکنه)\n\nپ.ن: هر چی به اخر ترم نزدیک میشه کلاس با حال تر میشه😃\n\n\n@WTIAU_ostad 🤓'),
(59, 'روشنک اعتمادپور', '👩🏼‍🏫 استاد #روشنک_اعتمادپور\n📚 دروس #علوم_پایه #فیزیک \n✅ نکات مثبت:\n۱- تمرین های هر فصل فیزیک را باید تحویل بدید ۲نمره درنظر گرفته شده براش\n\n❌ نکات منفی:\n۱- ۱۰ دقیقه دیرتر از ساعت کلاس برسید راه نمیدن\n۲- کلاس خشک و کسل کننده\n۳- جزوه خوب ولی نمونه سوال اصلا شبیه سوالای امتحان نیست\n۴- هیچ ارفاقی در نمره پایانی نمیکنند\n۵- جلسه اول میگن حضور غیاب نمیکنند ولی هر جلسه حضور غیاب میکنند و ممکن هست که حذف واحد هم بشید!\n\nپ.ن : تعداد زیادی از بچه ها میوفتن !!!\n@WTIAU_ostad 🤓'),
(60, 'پروانه خاکبیز', '👩🏼‍🏫 استاد #پروانه_خاکبیز\n📚 دروس #علوم_پایه #فیزیک\n\n✅ نکات مثبت :\n۱-جزوه کامل\n۲-سواد علمی بالا\n\n❌ نکات منفی :\n۱-کم حوصله\n۲-حضور غیاب میکنن\n۳-میخوای از کلاس بری بیرون باید ازش اجازه بگیری😐😐وگرنه جلو دَرَم رسیده باشی برت میگردونه سرِجات😑\n۴-بیشتریارو هم میندازه\n\nپ.ن : میگن که اگرم مجبورم بودی برندار....بی‌فایدس\n\n@WTIAU_ostad 🤓'),
(61, 'سمیه عسگری', '👩🏼‍🏫 استاد #سمیه_عسگری\n📚 دروس #علوم_پایه #فیزیک \n✅ نکات مثبت:\n۱-اخلاق خوب \n۲- کلاس خشک نیست\n\n❌ نکات منفی:\nندارد...!!\n\n@WTIAU_ostad 🤓'),
(62, 'فرناز حسين زاده', '👩🏼‍🏫 استاد #فرناز_حسين_زاده\n📚 دروس #علوم_پایه #فیزیک \n✅ نکات مثبت :\n۱-تمرين از كتاب مي دن تحويل بايد بدي نمره داره \n۲-درس دادنشون قابل فهمه\n۳-تمرين سر كلاس زياد حل ميكنن\n۴-جزوه شون قابل فهمه\n\n❌ نکات منفی :\n۱-اهميت حضور غياب\n۲-امتحانشون با جزوه يكم فرق داره و یه کم سخته ولي اگر تمريناشونو بدين ارفاق ميكنن\n\n@WTIAU_ostad 🤓'),
(63, 'پدرام راضی', '👨🏼‍🏫 استاد #پدرام_راضی\n📚 دروس تخصصی ، رشته #برق\n✅ نکات مثبت :\n۱- اخلاق خوب\n۲- تدریس مناسب\n۳- تعیین سوال برای امتحان و آمدن همان سوالات در امتحان\n۴- عدم اهمیت حضور غیاب\n\n❌ نکات منفی :\nندارد...!!!\n\nپ.ن: کلا نمره ها بالای ۱۵ چون سوالات عین سوالاتیه که دادن!!\n\n@WTIAU_ostad 🤓'),
