(65, 'محمود اوتادی', '👨🏼‍🏫 استاد #محمود_اوتادی\n📚 دروس  #علوم_پایه  #ریاضی\n✅ نکات مثبت :\n۱-حساس نبودن به حضور و غیاب ( اما بعضی وقتا حضور غیاب میکنن )\n۲-اخلاق مناسب\n۳-خشک نبودن کلاس\n۴-راحت بودن جزوه\n۵-شبیه بودن امتحان به جزوه\n\n❌ نکات منفی :\n۱-گاهی تمرین میدهند که حل کنید و تحویل دهید\n۲-کامل نبودن جزوه (نیاز به تهیه کتابی که خودشون معرفی میکنن)\n\n@WTIAU_ostad 🤓'),
(66, 'راضیه فولادی سپهر', '👩🏼‍🏫 استاد #راضیه_فولادی_سپهر\n📚 دروس #عمومی ،  #ادبیات\n✅ نکات مثبت :\n۱-خوش اخلاق و صبور\n۲-ارفاق مناسب\n۳-آخر ترم برای امتحان سوال ها رو مشخص میکنن\n۴-امتحان راحت\n۵-بیشتر سوالات امتحان از متن درس میباشد و معنی شعر کمتر است\n۶-حضور سر جلسه امتحان و راهنمایی کردن\n\n\n❌ نکات منفی :\n۱-اهمیت حضور در کلاس\n۲- پنج نمره از امتحان به ارائه کلاسی اختصاص دارد\n\nپ.ن : از روی کتاب درس میدن و فعالیت کلاسی براشون مهمه\n\n@WTIAU_ostad 🤓'),
(67, 'معصومه امیرخانلو', '👩🏼‍🏫 استاد #معصومه_امیرخانلو\n📚 دروس #عمومی #فارسی\n✅ نکات مثبت :\n۱-سواد بالا\n۲-درس دادن خوب\n۳-حضور غیاب انجام میدن ولی براش نمره در نظر نمیگیرن(نمیدونم بابت غیبت حذف میکنن یا نه🤔🤔)\n۴-برای امتحان مشخص میکنن که کجاها مهمن\n۵-نمره دادن خوب\n\n❌ نکات منفی :\n۱-نمره دادن به فعالیت کلاسی\n\n@WTIAU_ostad 🤓'),
(68, 'امیرمسعود حسین مردی', '👨🏼‍🏫 استاد #امیرمسعود_حسین_مردی\n📚 دروس تخصصی ، رشته #صنایع \n✅ نکات مثبت :\n۱-تدریس خوب(تا مطلب رو بچه ها متوجه نشن ازش رد نمیشن)\n۲-حل تمرین میذارن\n۳-امتحان میانترم حذفی میگیرن\n۴-نمره دادن عالی\n۵-جزوه عالی(خودشون جزوه نمیدن و خودتون باید نوت بردارین)\n۶-حضور غیاب انجام میدن ولی براشون مهم نیست و به حضور غیاب نمره میدن\n\n❌ نکات منفی :\nپروژه میدن و اجباریه\n\nپ.ن ۱ : تمرین میدن که اگر درست حل کنین ۲ نمره کمکی برای پایان ترم در نظر میگیرن\n\nپ.ن ۲ : جزوشون واسه درس جبر کامله و نیاز به نوت برداری نیست\n\n@WTIAU_ostad 🤓'),
(69, 'ژاله ابراهیمی نژاد', '👩🏼‍🏫 استاد #ژاله_ابراهیمی_نژاد\n📚 دروس #علوم_پایه #فیزیک  \n✅ نکات مثبت :\n۱-امتحان شبیه جزوه(عین جزوه نیست یه کم تفادت داره ولی شبیه جزوست)\n۳-سر امتحان راهنمایی میکنه\n۴-برای نمره دادن کمی ارفاق میکنن\n۵-هر جلسه حضور و غیاب میکنن اما براشون مهم نیست\n۶-تدریس خوب و قابل فهم\n\n❌ نکات منفی :\n۱-کلاس خشک و کسل کننده\n\n\n@WTIAU_ostad 🤓'),
(70, 'میلاد گرجی', '👨🏼‍🏫 استاد #میلاد_گرجی\n📚 دروس تخصصی ، رشته #صنایع \n✅ نکات مثبت :\n۱-چندبار امتحان میان ترم میگیرن تا بچه ها بتونن نمره خوب بگیرن\n۲-درس دادن عالی\n۳-خیلی خوب پاس میکنن\n۴-کلاسش خشک و کسل کننده نیست\n۵-سر امتحان راهنمایی میکنن\n\n❌ نکات منفی :\n۱-امتحان شبیه جزوه نیست ولی اگه بخونی عالی میشی\n\n@WTIAU_ostad 🤓'),
(71, 'محمود حاج حیدری', '👨🏼‍🏫 استاد #محمود_حاج_حیدری\n📚 دروس تخصصی ، رشته #صنایع \n✅ نکات مثبت :\n۱-جزوشون کامله \n۲-خوب نمره میدن\n\n❌ نکات منفی :\n۱-تمام تایم کلاس رو بچه هارو سر کلای نگه میدارن\n۲-از روی اسلاید سنگین درس میدن و اسلایدها رو هم نمیدن(برای درس استاتیک) و برای مقاومت مصالح از جزوه درس میدن\n\nپ.ن : برای امتحان چند سری سوال پیام نور بدون جواب دادن که جوابشو خود بچه ها باید پیدا کنن و امتحان ار اوناس\n\n@WTIAU_ostad 🤓'),
(72, 'آرمین دخت امین', '👩🏼‍🏫 استاد #آرمین_دخت_امین\n📚 دروس #عمومی\n✅ نکات مثبت :\n۱- امتحان راحت\n۲- سوالات از قسمتهای کلی\n۳- نمره دهی خوب\n۴- اخلاق مهربون\n۵- پروژه میدن که ۵ نمره داره که باید برید تو سایت امتحان بدین\n\n❌ نکات منفی :\n...!!\n\nپ.ن.۱:یکم جدی هستن نسبت به تدریس و تا آخر ساعت کلاسی درس میدن\n\nپ.ن.۲: از اساتید خانم هستند🙄\n\n@WTIAU_ostad 🤓'),
(73, 'فرید قدمی', '👨🏼‍🏫 استاد #فرید_قدمی\n📚 دروس تخصصی ، رشته #صنایع #مکانیک \n✅ نکات مثبت :\n۱-حضور غیاب اختیاریه و نمره داره\n۲-تمرین میدن ولی خیلی راحتن\n۳-کلاس خشک نیست\n۴-کلاس رو نسبتا زود تعطیل میکنن\n۵-به دانشجوها اهمیت میدن\n۶-بسیار با سواد\n\n❌ نکات منفی :\n...!!\n\nپ.ن.۱: بشدت به موبایل حساسن....حتی اگه تو شارژ باشه....و منفی میدن اگه ببینن\n\nپ.ن.۲: باید بخونی پاس شی وگرنه 9.9 هم 10 نمیده اگه نمره کلاسی و نمره حضور تو کلاس و پروژه نداشته باشی\n\n@WTIAU_ostad 🤓'),
(74, 'علیرضا رستمی', '👨🏼‍🏫 استاد #علیرضا_رستمی\n📚 دروس  #علوم_پایه \n✅ نکات مثبت :\n۱-خوب درس میدن\n۲-اخلاق خوب\n\n❌ نکات منفی :\n۱-ارفاق نمیکنن\n\nپ.ن : یکبار میگن که میخوان امتحان میان ترم بگیرن ولی نمیگیرن فقط میخوان بچه ها بخونن درس رو\n\n@WTIAU_ostad 🤓'),
(75, 'سروش الدین رضوی', '👨🏼‍🏫 استاد #سروش_الدین_رضوی \n📚 دروس تخصصی #صنایع\n✅ نکات مثبت :\n۱-فرمول هارو برای امتحان پایان ترم میده با برگه سوال\n۲-سر جلسه امتحان راهنمایی میکنه\n۳- بسیار فهمیده و خوش برخورد\n۴-بار علمی بالایی دارن\n۵-به سوالات درسی دانشجویان پاسخ میدن\n\n❌ نکات بد :\n۱-حضور و غیاب میکنه \n۲-هر جلسه درس جلسه پیش رو میپرسه \n۳-از روی اسلاید درس میده و جزوه نمیگه و باید نوت برداری کرد\n\nپ.ن : 13 نمره امتحان پایان ترم میگیره ، 7نمره اش برای حضور و غیاب و پرسش ها و فعالیت هاست \n\n@WTIAU_ostad 🤓'),
(76, 'بیژن رحمانی پرچکلاعی', '👨🏼‍🏫 استاد #بیژن_رحمانی_پرچکلاعی\n📚 دروس #علوم_پایه\n✅ نکات مثبت :\n۱- عدم اهمیت حضور و غیاب\n\n❌ نکات منفی :\n۱- تدریس نامناسب\n۲- جزوه نامناسب\n۳- در کلاس گوشی ممنوع\n۴- عدم ارفاق\n\nپ.ن: گویا دانشجویان زیادی ناراضی ان🙄\n\n@WTIAU_ostad 🤓'),
(77, 'سارا فتحی پور', '👩🏼‍🏫 استاد #سارا_فتحی_پور\n📚 دروس تخصصی ، رشته #کامپیوتر\n✅ نکات مثبت :\n۱- تدریس خوب\n۲- جزوه خوب\n۳- اخلاق خوب\n۴- امتحان خوب\n\n❌ نکات منفی :\nندارد...\n\n@WTIAU_ostad 🤓'),
(78, 'پیام مهدی پور کلدیانی', '👨🏼‍🏫 استاد #پیام_مهدی_پور_کلدیانی\n📚 دروس #علوم_پایه\n✅ نکات مثبت :\n۱- تدریس معمولی\n۲- جزوه معمولی\n۳- عدم اهمیت حضور و غیاب\n۴- حل تمرین نمره دارد ولی اجباری نیست\n۵- اخلاق مناسب\n\n❌ نکات منفی :\n۱- کلاس خشک\n\nپ.ن.۱: در این مورد معمولی یعنی اینکه ی عده نظرشون بوده خوب نیست یه عده گفتن خوبه\n\nپ.ن.۲: حضور و غیاب در درس ازمایشگاهشون مهمه و تو این درس معمولا اگه حاضر باشین همه جلسات و کارارو انجام بدین ۱۹ میدن گویا با ۲۰ میونه خوبی ندارن\n\nپ.ن.۳: فقط در صورتی درس ازمایشگاهو ۲۰ میدن که سوال امتیازی امتحان ازمایشگاهشون رو حل کنید\n\n@WTIAU_ostad 🤓'),
(79, 'فاطمه عرفاني واحد', '👩🏼‍🏫 استاد #فاطمه_عرفاني_واحد\n📚 دروس #عمومی\n✅ نکات مثبت:\n۱- تدریس طبق برنامه اولیه که میگن(كتاب مشخص ميكنن)\n٢-سر امتحان كمك ميكنن\n٣- امتحان فقط از ترجمه\n٤-رو خواني سر كلاس نمره دارد \n٥-سوالاي امتحان رو مشخص ميكنن\n۶-امتحان سبُک و آسون\n\n❌ نکات منفی:\n١-اهميت حضور و غياب\n\nپ.ن١: پنج نمره براي ارائه قرار ميدند و ارائه ميتونه تحقيق درباره شعرا و مشاهير ادبيات باشد\n\n@WTIAU_ostad 🤓'),
(80, 'ارش نبی زاده', '👨🏼‍🏫 استاد #ارش_نبی_زاده \n📚 دروس #علوم_پایه ، آمار و احتمالات مهندسی\n✅ نکات مثبت :\n۱- عدم حضور و غیاب\n۲- تدریس خوب\n\n❌ نکات منفی:\n۱- سرعت بالای تدریس مباحث\n۲- عدم ارفاق در نمره\n\nپ.ن.۱: دوستان راجعبه این استاد صحبت خوبی کرده بودن ولی امشب یه دوستمون گفتن این استاد رو از هر کی پرسیدن مناسب نیست🙄\n\nپ.ن.۲: کتاب میدن ۳۰۰ صفحه برا ۲ واحد😳😐\n\nپ.ن.۳: یکی دیگه گفت خود استاد میگه اگه کلاستون خوب باشه خوب نمره میدم نباشه میندازم😑\n\n@WTIAU_ostad 🤓'),
(81, 'بهاره پاشایی راد', '👩🏼‍🏫 استاد #بهاره_پاشایی_راد\n📚 دروس #علوم_پایه \n✅ نکات مثبت:\n۱- درصورت حل تمرین و جواب به سوال‌ها (حتی آسون) اعمال مثبت میکنند.\n۲-در صورت هماهنگ نبودن امتحان ، مثل جزوه سوال میدن.\n۳-میان ترم ۷ نمره‌ای از جزوه.\n۴-ارفاق در امتحان نهایی.\n۵-کم اهمیت بودن حضور غیاب\n\n❌ نکات منفی:\n...!!\n\nپ.ن:فن بیانشون یکم مناسب نیست🙄\n\n@WTIAU_ostad 🤓'),
(82, 'ایمان قیاسی', '👨🏼‍🏫 استاد #ایمان_قیاسی\n📚دروس تخصصی ، رشته #برق\n✅ نکات مثبت :\n۱- جزوه‌ی کامل\n\n❌ نکات منفی :\n۱- مهم بودن نظم در کلاس\n۲- ارفاق جزئی\n\nپ.ن: اگه‌ تلاش کنید باهاتون راه میاد\n\n@WTIAU_ostad 🤓'),
(83, 'حسن نوری اصفهانی', '👨🏼‍🏫 استاد #حسن_نوری_اصفهانی\n📚 دروس #علوم_پایه #ریاضی\n✅ نکات مثبت :\nندارد !!!\n\n❌ نکات منفی :\n۱- تدریس نامناسب (خیلی سریع و پراکنده درس میدن)\n۲- جزوه نامناسب\n۳- اخلاق نامناسب\n۴- عدم حضور به موقع سر کلاس و نگه داشتن دانشجویان بیشتر از وقت مقرر\n۴- کتاب معرفی می کنند، که حجم زیادی داره (به درد هم نمی خوره. منابع دیگه، کامل ترن‌!)\n۷- گوشی در کلاس ممنوع\n۸- عدم ارفاق در امتحان نهایی\n۹- به سختی پاس می کنند\n\nپ.ن.۱: با دخترا رابطه ی بهتری دارن کلا! 😂\n\nپ.ن.۲: یه دوستمون میگن ایشون اول ترم خیلی بچه هارو میترسونن ولی اخرش اون جوری ک میگن نیستن و پروژه و تخقیق میدن اگه ببرید پاس میکنن\n\nپ.ن.۳: تیکه کلامش (الی ماشاالله همتون میافتینه)😂\n\n@WTIAU_ostad 🤓'),
(84, 'ناهید کریمی', '👩🏼‍🏫 استاد #ناهید_کریمی\n📚 دروس تخصصی، رشته #عمران #طراحی_معماری_شهرسازی\n\n✅ نکات مثبت:\n۱.پروژه‌ی ۱۰ نمره‌ای.\n۲.تعداد جلسات کم.\n۳.تایم کلاس کم.\n۴.جزوه‌ی کم.\n۵.نمره‌ی بسیار خوبی میدن.\n\n❌ نکات منفی:\nندارد...\n\nپ.ن: بسیار رضایت داشتن\n@WTIAU_ostad 🤓'),
(85, 'علي شمسايي', '👨🏼‍🏫 استاد #علي_شمسايي\n📚دروس تخصصی، رشته #عمران #استاتیک\n✅نکات مثبت : \n۱- 13نمره كلاسي\n۲- هر جلسه از جلسه قبل كوييز ميگيره ١ نمره\n۳- حضور غياب مهم نيست \n۴- به حضور در كلاس هاي حل تمرين ٣ نمره ميده\n۵- مثل جزوه سوال ميده \n۶- سطح علمي بالا داره\n\n❌نكات منفي : \n...!!\n\nپ.ن.۱: تو كلاسش جديه و نبايد با همكلاسيت حرف بزني يا با گوشيت بازي كني عصبي ميشه حذف ميكنه\n\nپ.ن.۲: تيكه كلامش مهندسِ 😂😜\n\n@WTIAU_ostad 🤓'),
(86, 'محمد بهشتیان اردکانی', '👨🏼‍🏫 استاد #محمد_بهشتیان_اردکانی\n📚دروس تخصصی، رشته #عمران #مهندسی_آب_و_فاضلاب\n✅نکات مثبت : \n۱- حضور غیاب میکنه اما هرکی بخواد بعدش میتونه بره\n۲- سوالای مهم رو میگه که اکثرشون میاد تو امتحان\n۳- خوب توضیح میده\n۴- درک بالایی داشته باشی نمره بالایی میده \n\n❌ نکات منفی :\n...!!\n\nپ.ن.۱: در کل استاد باحالیه \nپ.ن.۲: تا غیبت به بالا داشته باشی نمره منفی داره\n\n@WTIAU_ostad 🤓'),
(87, 'میثم نصر', '👨🏼‍🏫 استاد #میثم_نصر\n📚دروس تخصصی ،رشته #مکانیک ، #سیالات #برنامه_نویسی\n✅نکات مثبت : \n۱- مهربون هستن\n۲- تدریس با پاورپوینت\n۳- ترجمه پاورپوینت و گفتن جزوه\n۴- باید برگه رو سیاه کنید تا پاس کنه\n\n❌ نکات منفی :\n...!!\n\nپ.ن: در کل دوستمون راضی بودن ازشون\n\n@WTIAU_ostad 🤓'),
(88, 'محمد امیان', '👨🏼‍🏫 استاد #محمد_امیان\n📚دروس تخصصی ،رشته #مکانیک ، #سیالات #ترمودینامیک\n✅نکات مثبت : \n۱- کمک در حد دو سه نمره با پروژه آسون\n۲- تدریس کامل سرفصل ها\n۳- برخورد خوب با دانشجوها\n\n❌ نکات منفی:\n۱- زمان امتحان(کم) ترمو۲ یک ساعت و نیم\n\nپ.ن: اکثر اصطلاحات درسی رو انگلیسی میگن \n\n@WTIAU_ostad 🤓'),
(89, 'الهام آهون بر', '👩🏼‍🏫 استاد #الهام_آهون_بر\n📚 دروس تخصصی، رشته #صنایع ، #مکانیک ، #علم_مواد #روش_تولید\n✅ نکات مثبت:\n۱- خوش اخلاق و با انرژی\n۲- سرکلاس فعال باشین دست باز صحیح میکنن\n۳- اگر سرکلاس نُت برداری کنید امتحان مشابه همونا میاد\n۴- خوب درس میدن\n\n❌ نکات منفی:\n\n...!!\n\nپ.ن.۱: حضور غیاب رو اهمیت میدن ولی اگه صحبت کنید حذف نمیکنه\n\nپ.ن.۲: از رو اسلاید درس میدن و جزوه خاصی نمیشه نوشت ولی اگه نُت بردارید خیلی خوبه\n\nپ.ن.۳: اکثرا راضی ان و نمره خوبی گرفتن دوستام\n\n@WTIAU_ostad 🤓'),
(90, 'ولی الله داوری', '👨🏼‍🏫 استاد #ولی_الله_داوری \n📚 دروس تخصصی ، رشته #عمران\n✅ نکات مثبت:\n...!!\n\n❌ نکات منفی:\n۱- اهمیت حضور و غیاب\n۲- تدریس ضعیف\n۳- دادن تمرین\n۴- نمره دادن ضعیف\n۵- جزوه نامناسب\n\nپ.ن: امتحان عملی سخت میگیرن اگه امتحان عملیشون رو خوب ندین احتمال افتادنتون بالاست🙄\n\n@WTIAU_ostad 🤓'),
(91, 'علی سلیمانی', '👨🏼‍🏫 استاد #علی_سلیمانی\n📚دروس تخصصی ،رشته #کامپیوتر\n✅نکات مثبت : \n۱- جزوه‌ی کامل\n۲- امتحان از جزوه\n۳- اخلاق خوب \n۴- برای حضور غیاب میتونید باهاشون هماهنگ کنید.\n\n❌ نکات منفی :\n...!!\n\nپ.ن: در کل دوستمون راضی بودن ازشون\n\n@WTIAU_ostad 🤓'),
(92, 'مرتضي صالح كندزی', '👨🏼‍🏫 استاد #مرتضي_صالح_كندزی\n📚دروس تخصصی، رشته #عمران #استاتیک\n✅نکات مثبت: \n۱- تدريس خوبي دارن\n۲- ارفاق ميكنن تا حدي\n\n❌نكات منفي: \n۱- حضور غياب ميكنن \n۲-با پسرا ميگن دوستان زياد خوب نيست \n\nپ.ن.۱: دخترا رو با ١٨-٢٠ ميگن پاس كنه 😶\n\nپ.ن.۲: یکی از دوستان گفتن ایشون زیاد هم خوب درس نمیده و توصیه کردن اگر دفعه اول تون هست با استاد کندزی برندارید اگر قبلا داشتید و تا حدودی بلدید  بردارید و پروژه هم بگیرید و انجام بدید خوبه..\n\n@WTIAU_ostad 🤓'),
(93, 'نويد حسينی', '👨🏼‍🏫 استاد #نويد_حسينی\n📚دروس تخصصی، رشته #عمران #ديناميك\n✅نکات مثبت: \n۱- تمريناي جلسه قبلتو بدي  و حضور داشته باشي پاس ميكنه\n۲- حضور غياب ميكنه اما از قبل هماهنگ كني ميتوني  بري \n۳- ارفاق ميكنه\n۴- جزوه خوبي داره \n۵- سطح علمي بالا داره\n\n❌نكات منفي: \n...!!\n\nپ.ن.۱.:دوستان نظرشون این بوده نسبت به استادای دیگه این درس بهترن🙄\n\nپ.ن.۲: تمرين ميده اگه انجام ندي ميندازتت\n\n پ.ن.۳: تعداد قبوليه دخترا گويا بيشتر از پسراس طبق گفته دوستان😐😶\n\n@WTIAU_ostad🤓'),
(94, 'نادر محمدی', '👨🏼‍🏫 استاد #نادر_محمدی\n📚 دروس تخصصی، رشته #مکانیک ، #دینامیک_و_مقاومت_مصالح\n✅ نکات مثبت:\n...!!\n\n❌ نکات منفی:\n۱- درس دادن از روی اسلاید انگلیسی و توضیح ندادن\n۲- بد نمره دادن\n۳- واسه امتحان هم گویا باید کتاب خوند\n\nپ.ن: قبل از اینکه دانشگاه ما بیان دانشگاه پرند تدریس میکردن از این نظر میشه یه امید هایی داشت دوستانی که مجبور شدن با ایشون بردارن تلاششون رو بکنن ایشالا پاسن\n\n@WTIAU_ostad 🤓'),
(95, 'میرفردوس', '👨🏼‍🏫 استاد #میرفردوس\n📚 دروس تخصصی، رشته #کامپیوتر\n✅ نکات مثبت: \n۱- خوش برخورد\n۲- امتحان عین جزوه یا قسمت های مهمی که میگن هستش\n۳- نمره دهی مناسب\n۴- چند جلسه درمیون حضور و غیاب میکنن میگن حذف میکنن اما اگه باهاشون یکم صحبت کنید راضی میشن حذف نکنن.\n\n❌ نكات منفي: \nندارد...!!\n\n پ.ن.۱: سعی کنید تو چشمشون باشید اینجوری تو نمره ارفاق بیشتری میکنن اما اگه نباشید هم نمره ای کم نمیکنن.\n\nپ.ن.۲: خودم به شخصه یه درسشون رو کلا نرفتم سر کلاسا آخرین جلسه رفتم صحبت کردم اول گفتن حذف میکنن اما بعد قبول کردن حذف نکنن😕\n\nپ.ن.۳: در رابطه با درس زبان تخصصی حتما باید یه روز برید تا روز ارائه رو براتون تعیین کنن و در روز مشخص شده هم برید ارائه بدین حتما حساسن رو این مورد🙄\n\n@WTIAU_ostad🤓'),
(96, 'مهناز ترابی', '👩🏼‍🏫 استاد #مهناز_ترابی\n📚 دروس #علوم_پایه ، #فیزیک \n✅ نکات مثبت:\n...!!\n\n❌ نکات منفی:\n۱- تدریس نامناسب\n۲- بد نمره دادن\n۳- عمل نکردن به وعده های قبل امتحان(مثلا اگر فلان کار رو بکنید پاس میکنم و ...)\n\n@WTIAU_ostad 🤓'),
(97, 'پریسا دانشجو', '👩🏼‍🏫 استاد #پریسا_دانشجو\n📚 دروس تخصصی ، رشته #کامپیوتر\n✅ نکات مثبت:\n۱- جزوه‌ی کم\n۲- امتحان در سطح جزوه\n۳- زیاد اذیت نمیکنه\n\n❌ نکات منفی:\n۱- ارفاق زیاد نمیکنه\n\nپ.ن: خیلی زود کلاسو تموم میکنن!!!\n\n@WTIAU_ostad 🤓'),
(98, 'مهسا کربلایی حسن', '👩🏼‍🏫 استاد #مهسا_کربلایی_حسن\n📚 دروس تخصصی، رشته #صنایع #اقتصاد\n✅ نکات مثبت:\n۱- خوش اخلاق\n۲- امتحان=جزوه\n۳- با سواد\n۴- اگر اذیت نکنید کلاسش خیلی باحال\n\n❌ نکات منفی:\n...!!\n\nپ.ن.۱: اگر موضوعی که مشخص میکنه رو ارائه بدین کلی تو نمره گرفتن جلو میوفتین منتها اگر ندین نمرشو اصلا نمیگیرین.\n\nپ.ن.۲: دانشجو ها تو ذهنش میمونن.\n\n@WTIAU_ostad 🤓'),
(99, 'فاطمه سپهری', '👩🏼‍🏫 استاد #فاطمه_سپهری\n📚 دروس تخصصی ، رشته #کامپیوتر\n✅ نکات مثبت:\n۱- خوش اخلاق\n۲- امتحان مثل جزوه\n۳- جزوه‌ی کم\n۴- نمونه سوال داده میشه\n\n❌ نکات منفی:\n...!!\n\nپ.ن.۱: در خصوص استاد سپهری اطلاعات مختلفی به دستم رسیده که کاملا باهم تضاد دارن. \nاگر باز هم تونستید اطلاعات به دست ما برسونید.\n\nپ.ن.۲:\n۱- اخلاق نامناسب\n۲- کلاس های خشک\n۳- جزوه‌ی نامناسب\n۴- ارفاق نکردن در امتحان \n\nپ.ن.۳:\n۱- سخت‌گیری در حضور و غیاب\n۲- کلاس معمولی\n۳- جزوه‌ی آسون\n۴ تقریبا سوالات رو میدن و از کتاب مشخص میکنن\n\n@WTIAU_ostad 🤓'),
(100, 'حمید شاهوردی طرخورانی', '👨🏼‍🏫 استاد #حمید_شاهوردی_طرخورانی\n📚دروس تخصصی ،رشته #کامپیوتر\n✅نکات مثبت : \n۱. حل تمرین سر کلاس\n\n❌ نکات منفی :\n۱. کلاس حدودا خشکی دارن\n۲. سختگیر\n۳. به راحتی نمره نمیدن\n\nپ.ن: اگر سر کلاس تمارین رو حل کنید و در چششون باشید بیشتر کمک میکنن\n\n@WTIAU_ostad 🤓'),
(101, 'سیروس نبی‌زاده ولوکلائی', '👨🏼‍🏫 استاد #سیروس_نبی‌زاده_ولوکلائی\n📚دروس #عمومی\n✅نکات مثبت : \n۱. کلاس خوب\n۲. تدریس مناسب\n۳.اخلاق و برخورد عالی\n٤.كمكت ميكنه كه پاس بشي\n\n❌ نکات منفی :\n...!!\n\nپ.ن.۱: حضور و غیاب تا ۳ جلسه مشکلی نداره و برای بیشتر از این تعداد باید باهاشون صحبت کنید.\nپ.ن.٢: از جزوه اي كه ميدن امتحان ميگيرن و اكثر جواب هاي سوالا كوتاهه \nپ.ن.٣: در كل از ايشون راضي بودن دوستان 😊\n\n@WTIAU_ostad 🤓'),
(102, 'مریم اشتری ماهینی', '👩🏼‍🏫 استاد #مریم_اشتری_ماهینی\n📚 رشته #کامپیوتر\n✅ نکات مثبت:\n۱. امتحان عین جزوه\n۲. جزوه‌ی سبک‌ و کامل\n۳. سطح علمی بالا \n۴. آزمون میان ترم + نمره\n\n❌ نکات منفی:\n۱. تاکید بر حضور غیاب\n۲. کل تایم کلاس رو درس میدن\n\n@WTIAU_ostad 🤓'),
(103, 'مسعود باقر زاده', '👨🏼‍🏫 استاد #مسعود_باقر_زاده\n📚 دروس #نقشه_کشی رشته ها\n✅ نکات مثبت:\n۱. نمره دادن خوب\n۲. میشه 20 گرفت\n۳. لهجه شیرین 😬\n\n\n❌ نکات منفی:\n...!!\n\n@WTIAU_ostad 🤓'),
(104, 'پارمیس نراقی', '👩🏼‍🏫 استاد #پارمیس_نراقی\n📚دروس تخصصی، رشته #معماری\n✅ نکات مثبت:\n۱. خیلی خوب درس میدن(به خصوص برای درس عملی طرح معماری کلی چیز یاد میگیری ازشون)\n۲. خیلی خوب نمره میدن\n۳. میان ترم میگیرن\n۴. زود کلاس و تموم میکنن\n۵. ارتباطشون با دانشجو عالیه و خیلی صمیمی هستن با همه\n۶. ارتباط با بچه ها از طریق نماینده کلاس و پاسخگویی در اسرع وقت\n۷. همه ی تلاششونو میکنن که همه درسو  یاد بگیرن \n۸. برای واحد های نظری جزوه ی خوب و مختصر مفید میدن \n۹. برای درس های عملی خیلی با بچه ها راه میان برای پروژه ها\n۱۰. در مورد درس کاربرد کامپیوتر هم اتوکد درس میدن هم رویت که کمتر استادی این کار رو میکنه\n\n❌ نکات منفی:\n...!!\n\n@WTIAU_ostad 🤓'),
(105, 'مسعود فرهمند زاده', '👨🏼‍🏫 استاد #مسعود_فرهمند_زاده\n📚 دروس #مبانی_دفاع_مقدس\n✅ نکات مثبت:\n۱- نمره دادن خوب\n۲- تدریس خوب\n۳- جزوه خوب\n\n❌ نکات منفی:\n۱- اهمیت شدید حضور و غیاب\n\n@WTIAU_ostad 🤓'),
(106, 'مجيد كرمي', '👨🏼‍🏫 استاد #مجيد_كرمي\n📚 دروس #علوم_پایه ، #کامپیوتر\n✅ نکات مثبت:\n۱- سطح علمی بالا\n٢- جزوه كامل و مناسب و حل تمرين بسيار\n٣- تدريس عالي\n\n❌ نکات منفی:\n۱- اهمیت حضور و غیاب\n۲- كلاس هاي كسل كننده\n٣- پرسش سر كلاس\n٤- نسبتا بي حوصله (ولي به سوالات پاسخ ميدهند)\n٥- آخر هر جلسه يه سري تمارين براي جلسات بعد ميدن\n\nپ.ن.۱: معمولا درس رياضي پيش دانشگاهي رو پاس ميكنن(بستگي به دانشجو و فعاليتش در كلاس داره)\n\nپ.ن.٢: درس معادلات ديفرانسيل و راحت پاس نميكنن ، خيلي بايد خوب باشيد كه كمكتون كنن\n\n@WTIAU_ostad 🤓'),
(107, 'سينا دامي', '👨🏼‍🏫 استاد #سينا_دامي\n📚 دروس تخصصی ، رشته #کامپیوتر\n✅ نکات مثبت:\n۱- اگه اذیتشون نکنید سر كلاس بهتون گیر نمیدن\n٢- با نمره معمولي  پاس ميكنن\n\n❌ نکات منفی:\n۱- اهمیت حضور و غیاب (حضور نمره دارد)\n۲- جزوه سر كلاس نميدهند\n۳- تدریس نامناسب\n۴-کلاس ها فوق العاده کسل کنندست\n\nپ.ن.١: براي هر شخص پنج نمره برنامه نويسي قرار ميدند كه روش خيلي حساسن و اگر انجام بشه احتمال پاس كردنش زياده ، در غير اينصورت شايد سخت نمره بدن(مخصوص درس برنامه سازي پيشرفته)\n\nپ.ن.٢: يكسري جزوه طولاني بصورت pdf ميدن و ميگن امتحان نسبتا شبيه سوالات داخل pdf هستش ، ولي خوندنش سخته چون طولانيه\n\n\n( همه موارد براي درس برنامه سازي پيشرفته هستش )\n\n@WTIAU_ostad 🤓'),
(108, 'محمد جواد خوشرو', '👨🏼‍🏫 استاد #محمد_جواد_خوشرو\n📚 دروس تخصصی، رشته #کامپیوتر\n✅ نکات مثبت:\n۱. حضور غیاب نمیکنن\n۲. بار علمی خوب و زیاد\n۳. خوب درس میدن\n\n❌ نکات منفی:\n۱. به ورودی جدیدا حساسن (کلا به گوشی دراوردن و نشستن دختر/پسر پیش هم و‌...)\n۲.پایین تر از حدس شما نمره میدن و حدود نمره ۱۰ ۱۲ مد نظرتون باشه.\n۳. تعداد نمرات بالای کلاس کم\n\n پ.ن.۱: برای هر درس نوع مختلفی تمرین میدن (ارائه ، تمرین‌و...) که براشون از امتحان مهمتره.\n\n\n@WTIAU_ostad 🤓'),
(109, 'حامد رئیسی فرد', '👨🏼‍🏫 استاد #حامد_رئیسی_فرد\n📚 دروس تخصصی، رشته #مکانیک  #دینامیک_استاتیک_ارتعاشات\n✅ نکات مثبت:\n۱. حضور غیاب رو کنار میاد باهاتون و ساعت کلاس\n۲. مهربون و خوش برخورد\n۳. بدونه بلدین پاس میکنه\n۴. سرجلسه امتحان خیلی کمک میکنه\n۵. با سواد \n\n❌ نکات منفی:\n۱. تدریس سطح بالا(قبلا ؛ الان رو نمیدونم)\n۲.امتحان سطح بالا(قبلا)\n\n پ.ن.۱: مثال های حل شده کتاب رو بخونید برا امتحانش\n\nخاطره:اولین ترمی که دینامیک داشتم؛ میدونستم ک سیاه کنم نمره میده؛ رفتیم سر جلسه سوالارو دیدیم فرمول هم آزاد بود سر جلسه؛ همه همدیگه رو نگاه میکردیم حتی نمیدونستیم الان کدوم فرمول رو باید بزاریم چجوری سیاه کنم اصن(استاد سیاه کردنم رو دستم نیس) 😂😂😂 ولی الان خیلی بهتر شده..\n \n@WTIAU_ostad 🤓'),
(110, 'سروش پرویزی', '👨🏼‍🏫 استاد #سروش_پرویزی\n📚 دروس تخصصی، رشته #صنایع ، #علم مواد\n✅ نکات مثبت:\n۱- از روی اسلاید درس میده\n۲- با سواد \n\n❌ نکات منفی:\n۱- اذیت کنی لج میکنه باهات\n۲- هر جلسه از اکثریت میپرسه\n۳- کلاس خشک و جکاش بی مزه\n\n پ.ن.۱: امتحان کمترش مسئله هست\n\nپ.ن.۲: اگر هر جلسه بخونی مشکلی پیش نمیاد\n\n@WTIAU_ostad 🤓'),
(111, 'طلایه طبیبی', '👩🏼‍🏫 استاد #طلایه_طبیبی\n📚 دروس تخصصی ، رشته #کامپیوتر\n✅ نکات مثبت:\n۱- نمره دادن خوب\n۲- جزوه خوب\n۳- راه اومدن با دانشجویان\n۴- سواد بالا\n۵- امتحان مثل جزوه\n\n❌ نکات منفی:\n۱- رو حضور و غیاب حساس هستند\n\nپ.ن.۱: معمولا نمیذارن کلاسشون رو با ساعت دیگه جا به جا کنید و تمام تایم کلاسی رو درس میدن البته آنتراک هم میدن.\n\nپ.ن.۲: تمرین میدن که اجباری نیست اما اگه حل کنید نمره داره! \n\n@WTIAU_ostad 🤓'),
(112, 'علیرضا موسوی', '👨🏼‍🏫 استاد #علیرضا_موسوی\n📚 دروس تخصصی ، رشته #کامپیوتر\n✅ نکات مثبت:\n۱- بسیار خوش اخلاق\n۲- جزوه مناسب\n۳- درس رو چند بار توضیح میدن برای دانشجویان\n۴- جو کلاس خوب\n۵- امتحان مثل جزوه\n\n❌ نکات منفی:\n...!!\n\nپ.ن.۱:تمرین میدن که دو نمره داره اگه ایمیل کنید نمرشو میگیرید و امتحان از ۱۸ نمرست...سر امتحان به دانشجویان کمک میکنن...ب اعتراضات از طریق گروه تلگرامی رسیدگی میکنند\n\nپ.ن.۲: سر کلاسشون باشید خوب درس رو میفهمید.\n\n@WTIAU_ostad 🤓'),
(113, 'زهرا محمودی', '👩🏼‍🏫 استاد #زهرا_محمودی\n📚 دروس #علوم_پایه\n✅ نکات مثبت:\n۱- جزوه ی خوب و کامل\n۲- سواد علمی بالا\n۳- حل تمارین زیاد در کلاس\n۴- روی حضور و غیاب تا حدودی حساس هستند (تا حدودی🙄)\n۵- اگه اذیتشون نکنید خوش برخورد هستند\n\n❌ نکات منفی:\n۱- کل تایم کلاسی رو درس میدن بی آنتراک\n۲- به دخترا راحت نمره میدهند\n۳- عدم ارفاق در نمره\n\n@WTIAU_ostad 🤓'),
(114, 'محمد خدایی', '👨🏼‍🏫 استاد #محمد_خدایی\n📚 دروس تخصصی ، رشته #کامپیوتر #برق\n✅ نکات مثبت:\n۱. سطح علمی بالا\n\n❌ نکات منفی:\n۱. جزوه‌ی زیاد\n۲. کلاس کسل کننده\n۳. تاکید بر تکمیل قسمت‌های درسی غیر مهم\n۴. ارائه ترجمه متون تخصصی که براشون مهم هست\n۵. تمرین زیاد\n۶. امتحان سخت\n\n@WTIAU_ostad 🤓'),
(115, 'بتول گلزاری سرخه', '👩🏼‍🏫 استاد #بتول_گلزاری_سرخه\n📚 دروس #معارف ، #اندیشه_اسلامی\n✅ نکات مثبت:\n۱- کلاس خشک نیست\n۲- با سواد\n۳- خلاصه درس میدن\n۴- روز اول چند تا فصل رو برای ارائه مشخص میکنند و هرکس ارائه بده ۲نمره به برگه اضافه میکنند\n۵- امتحان تستی و ۳ یا ۴ سوال تشریحی\n۶- حذفیات رو مشخص میکنند\n۷- قسمت هایی که تستی یا تشریحی میاد رو مشخص میکنند\n\n❌ نکات منفی:\n۱- حضور غیاب مهم هست ولی تا ۴تا اگه باهاشون صحبت کنید راه میان\n۲ - به موقع حضور داشتن در کلاس براشون مهمه\n۳ - عدم ارفاق در نمره\n\n@WTIAU_ostad 🤓'),
(116, 'پخشان اسپوکه', '👩🏼‍🏫 استاد #پخشان_اسپوکه\n📚 دروس علوم_پایه ، #آز_فیزیک #فیزیک_عمومی\n✅ نکات مثبت:\n۱- خوش برخورد\n۲- کلاس شاداب\n۳- امتحان ساده(البته برای فیزیک امتحان هماهنگه)\n۴- دست باز صحیح میکنه\n\n❌ نکات منفی:\n۱- اهمیت حضور و غیاب\n\nپ.ن.۱: هر جلسه گزارش کار میخوان🙄(#آز_فیزیک)\n\n@WTIAU_ostad 🤓'),
(117, 'اسفندیار جاسمی زرکانی', '👨🏼‍🏫 استاد #اسفندیار_جاسمی_زرکانی\n📚 دروس تخصصی، رشته #معماری #عمران ،مصالح،روستا،مقاومت سازه \n✅ نکات مثبت:\n۱. به حضور غیاب خیلی اهمیت میدن و بدون این که هر جلسه حضور غیاب کنن یادشون میمونه کی بود سر کلاس و کی نبود\n۲. بیشتریارو پاس میکنن و معمولا اگر تو امتحان خوب ننویسی و غیبت زیاد داشته باشی ممکنه بندازن\n۳. کلاسشون شاده تقریبا \n۴. امتحاناشون آسونه و دقیقا مثل جزوه اس \n۵. معمولا هم با ارفاق نمره میدن\n\n\n❌ نکات منفی:\n...!!\n\nپ.ن.۱: جزوه شون یکم سنگینه و با بقیه استادا فرق میکنه ولی کامل سیلابس رو درس میدن\n\n@WTIAU_ostad 🤓'),
(118, 'سینا قاضی زاده', '👨🏼‍🏫 استاد #سینا_قاضی_زاده\n📚 دروس تخصصی، رشته #معماری\n✅ نکات مثبت:\n۱. خیلی خوب نمره میدن\n۲. هیچ کس رو نمیندازن تا اونجایی که میدونم\n۳. خوب توضیح میدن\n۴. کلا خیلی آرومه کلاسشون\n۵. سنشون کمه\n۶. پروژه ی پایان ترم سبک تر از بقیه اساتید\n۷. استاندارد هایی که توی نویفرت هست رو خیلی خوب توضیح میدن و دلیلش رو میگن\n۸. برای کسایی که میخوان رابطه ی صمیمانه با استادشون داشته باشن خیلی خوبن\n\n\n❌ نکات منفی:\n۱. کرکسیون ضعیف \n۲. کرکسیون تکی یا گروهی فقط اول ترم انجام میشه و بعد از چند جلسه کلا بیخیال میشن...(سر کلاس ما که اینطور بود!)\n۳. باید حتما ازشون بپرسی تا نقطه ضعف یا نقطه قوتت رو بگن\n\n@WTIAU_ostad 🤓'),
(119, 'هاله مرادی', '👩🏼‍🏫 استاد #هاله_مرادی\n📚 دروس #علوم_پایه\n✅ نکات مثبت:\n۱. تدریس عالی و روی نظم\n۲. خیلی خوش اخلاق هستن و کمک دانشجوها میکنن\n۳. از رو کتاب درس میدن\n۴. خیلی براشون اهمیت داره که دانشجوها درس رو بفهمن\n۵. کلاسشون جو بسیار خوبی داره\n۶. حضور و غیاب چند جلسه درمیون انجام میدن و سعی میکنن زیاد سخت نگیرند\n۷. امتحانشون متوسط (نه آسون نه سخت)\n۸. سر جلسه امتحان خیلی به دانشجوها کمک میکنن\n\n❌ نکات منفی:\nاصلا ندارند...!!!!!👌\n\nپ.ن: خدا همچین اساتیدی رو بیشتر کنه تو دانشگاهمون و نصیب همه بشه☺❤\n\n@WTIAU_ostad 🤓'),
(120, 'مرتضی جداری عطاری', '👨🏼‍🏫 استاد #مرتضی_جداری_عطاری\n📚 دروس تخصصی، رشته #عمران اقتصاد،مكانيك،سيالات،متره\n✅ نکات مثبت: \n١.كلاسهاي حل تمرين نمره اضافه داره و تاثير داره\n\n❌ نكات منفي: \n١.خوب تدريس نميكنن\n٢.جزوه مناسب ندارن براي امتحان و حتما بايد جزوه اساتيد ديگر رو تهيه كنيد\n٣.حضور غياب مهمه\n\n\n پ.ن.۱: درس هاي اختصاصي مثل مكانيك سيالات رو راحت پاس نميكنن به شرطي كه بدونن دانشجو واقعا درس رو ياد گرفته\n\nپ.ن.٢: اما  درس هايي مثل اقتصاد \nتو نمره دادن سخت گيري نميكنن و خيلي دست باز صحيح ميكنن\n\n@WTIAU_ostad 🤓'),
(121, 'محمدمهدی امين پور', '👨🏼‍🏫 استاد #محمدمهدی_امين_پور\n📚 دروس تخصصی، رشته #عمران #مکانیک ،مهندسی_پی ، خاك١_٢\n✅ نکات مثبت: \n۱. خوش اخلاق\n۲. به حضور غياب خيلي اهميت نميده ( بعضي از دوستان ميگن حضور غياب كنن هر جلسه )\n۳. اگر مسائل سر كلاس رو بتوني حل كني مثبت داره و تاثير زيادي داره تو نمره\n۴. خيلي خوب تدريس ميكنن\n۵. جزوه کامل\n\n❌ نكات منفی: \n١. اگر امتحان سخت بگيره و كل كلاس هم بيفتن براش مهم نيست\n٢. سطح امتحان بالاتر از جزوه و سخت\n٣. كلاسشون كسل كننده است\n۴. كسي سر كلاس حرف بزنه يا از گوشي استفاده كنه تذكر نميدن و منفي ميذارن و توي امتحان لحاظ ميكنن(امكان افتادن هم هست )😶😐\n\n\n پ.ن.۱: تو درسي مثل مهندسي پي جزوه باز نيست و فقد بعضي از فرمولا رو ميده\n\nپ.ن.٢: امتحان ميان ترم ميگيرن كه مروري بشه براي ترم\n\n@WTIAU_ostad🤓'),
(122, 'علی مقصودی', '‌‎👨🏼‍🏫 استاد #علی_مقصودی\n‌‎📚 دروس تخصصی، رشته #عمران\n‌‎✅ نکات مثبت: \n‏۱- در صورت غيبت نكردن ١ نمره اضافه ميكنن \n‌‎۲- جزوه كامل و سوالاي امتحان رو پوشش ميده \n‏\n‌‎❌نكات منفي: \n‌‎۱- خوب درس نميدن \n\nپ.ن۱: تمرين ميدن آخر هر فصل و خيلي دقيق ى صدم نمره شو ميدن مثلا ١.٠٢ صدم از ٣ نمره \n\nپ.ن.۲: جزوه استاد رو كامل بلد باشين نمره خوبي ميتونين بگيرين\n\nپ.ن.۳: در كل دوستان زياد راضي نيستن \n \n@WTIAU_ostad 🤓'),
(123, 'امید شکراللهی', '👨🏼‍🏫 استاد #امید_شکراللهی\n📚 دروس تخصصی ، رشته #کامپیوتر\n✅ نکات مثبت:\n۱- ارفاق جزئی \n۲- عدم سختگیری در مدیریت کلاس\n\n❌ نکات منفی:\n۱- جزوه پراکنده و پیچیده\n۲- سخت گیری بابت ارسال تمارین به ایمیل\n۳- تدریس ضعیف\n۴- عدم دسترسی به استاد با ایمیل و عدم پاسخگویی\n\n@WTIAU_ostad 🤓'),
(124, 'شرفعلی جلولی', '‌‎👨🏼‍🏫 استاد #شرفعلی_جلولی\n‌‎📚 دروس  #انقلاب_اسلامی #آشنایی_با_مبانی_دفاع_مقدس\n‌‎✅ نکات مثبت: \n۱. اگر كتابشو بخري ١٠ نكره ميده \n‌‎۲. حضور غياب مهمه اما صحبت كني مشكلي نداره غيبت\n٣. ارفاق ميكنه\n٤. امتحانش نصفش تستيه ،نصف تشريحي \n‌‎\n\n‌‎❌ نكات منفي: \n‌‎١. نمره خودتو ميده \n٢. ولی اگه خوشش نیاد ازت حذف میکنه\n\nپ.ن.۱: خيلي ساده و بامزه اس \n\nپ.ن.٢: در كل استاد ٥٠ ٥٠ ايه  \n\nپ.ن.۳: بعضی درساش امتحانش کامل تشریحیه\n\n@WTIAU_ostad 🤓'),
(125, 'محمدرضا خلیل پور', '👨🏼‍🏫 استاد #محمدرضا_خلیل_پور\n📚 دروس تخصصی ، رشته #مکانیک \n✅ نکات مثبت:\n۱- تدریس کامل و عالی\n۲- سر کلاسش اگ برید درس رو میفهمین\n\n❌ نکات منفی:\n۱- سخت گیر سخت گیر سخت گیر\n۲- امتحان سخت\n۳- نمره هارو دیر میده \n\nپ.ن: پاس کردن باهاش زحمت کشیدن میخواد\n\n@WTIAU_ostad 🤓'),
(126, 'مهرخ الیاسی اسب فروشانی', '👩🏼‍🏫 استاد #مهرخ_الیاسی_اسب_فروشانی\n📚 دروس #علوم_پایه #فیزیک \n✅ نکات مثبت:\n...!!\n\n❌ نکات منفی:\n...!!\n\nپ.ن.۱: یکی از دوستان گفتن خصوصیت منفی نداره درس دادنش عالیه خوب نمره میده حضور غیاب مهمه براشون!!\n\nپ.ن.۲:یکی از دوستان هم گفته خصوصیت مثبت نداره کلاسشون خشکه و خیلی درس میدن، راهنمایی بابت امتحان نمیکنن!!\n\nپ.ن۳: من با نظر دوستمون ک گفتن استاد خوبیه بیشتر موافقم چون یادمه ترم یک دوستانم نمره های خوبی گرفتن..\n\nپ.ن.۴: در قسمت های بعدی با پدیده های جدیدتری از اساتید در خدمتتان هستیم🤦‍♀😂\n\n@WTIAU_ostad 🤓'),
(127, 'فرناز عراقی', '👩🏼‍🏫 استاد #فرناز_عراقی \n📚 دروس #علوم_پایه #فیزیک \n✅ نکات مثبت :\n۱- خوش اخلاق\n۲- تدريس خيلي خوب و قابل فهمه \n۳- تمرین حل میکنن\n۴- جلسه حل تمرينم ميزارن از بچه ها انتخاب ميكنن يكيو بياد حل كنه تمرينا رو\n۵- امتحان از جزوه و تمرينها \n۶- نمره دادنشون خوبه\n\n❌ نکات منفی :\n۱- حضور غياب براشون اهميت داره \n۲- كل تايم كلاس نگه ميدارن\n\nپ.ن.۱: از كتاب چندتا تمرين مشخص ميكنن بايد تحويل داد( اخر ترم ميگن همونا رو بخوني پاس ميشی )\n\nپ.ن.۲: میگن حضور غیاب میکنن اما مهم نیست براشون و اینکه آنتراک هم میدن اتفاقا🤔😕\n\nپ.ن.۳:کسی رو هم حذف نمیکنن👌🙄\n\n@WTIAU_ostad 🤓'),
(128, 'ابولفضل ریاحی نوری', '👨🏼‍🏫 استاد #ابولفضل_ریاحی_نوری\n📚 دروس تخصصی ، رشته #عمران #زمین_شناسی_مهندسی\n✅ نکات مثبت:\n۱- رفتن یا نرفتن سر کلاس فرقی نداره اما در هر صورت حضورتون باید بخوره\n۲- تدریس از روی اسلاید\n\n❌ نکات منفی:\n۱- نمره خودتون رو میدن باید بخونین تا پاس شین\n\n@WTIAU_ostad 🤓'),
(129, 'فرهنگ فرهبد', '👨🏼‍🏫 استاد #فرهنگ_فرهبد\n📚 دروس تخصصی ، رشته #عمران #سازه_فولادی\n✅ نکات مثبت:\n۱- تدریس عالی\n۲- نمره خودتون رو میدن اما اهل انداختن نیستن معمولا\n۳- حضور و غیاب میکنن اما زیاد مهم نیست\n\n❌ نکات منفی:\n...!!\n\nپ.ن: در کل دوستان راضی بودن\n\n@WTIAU_ostad 🤓'),
(130, 'الهام الیکا', '👩🏼‍🏫 استاد #الهام_الیکا\n📚 دروس #علوم_پایه \n✅ نکات مثبت:\n۱- تدریس خوب\n۲- جزوه کامل\n۳- گرفتن امتحان میان ترم (هر کی بخواد امتحان میده هر کی نخواد هم نمیده)\n۴- اگه امتحان میان ترمتون رو بدید کمک میکنن به پاس شدنتون\n۵- امتحان کپی جزوه ( بدون تغییر اعداد )\n\n❌ نکات منفی:\n...!!\n\nپ.ن.۱: استاد خوبی هستن و برای امتحانشون اگه جزوه رو دقیق بخونید نمره میگیرید!\n\nپ.ن.۲: یکی از دوستان تضمین کردن پاس شدن رو فقط در صورتی میافتین که برگه سفید باشه🙄😂\n\n@WTIAU_ostad 🤓'),
(131, 'فواد ملایی', '👨🏼‍🏫 استاد #فواد_ملایی\n📚 دروس تخصصی ، رشته #مکانیک #دینامیک_مقاومت\n✅ نکات مثبت:\n۱- اخلاق نسبتا خوب\n۲ - باهاش رفیق شین سر کلاس خودی نشون بدین احتمال پاسی هست\n\n❌ نکات منفی:\n۱- تدریس نه چندان خوب\n۲- تدریس بیشتر از سر فصل های مشخص شده(مثلا تو مقاومت۱ روش انرژی میگفت ک واسه مقاومت۲) \n۳- کلا باید خودتون بخونید از کتاب و اینا \n\nپ.ن: انتخاب اول تون نباشه اگه مجبور بودید بردارید \n\n@WTIAU_ostad 🤓'),
(132, 'سمانه پرواز', '👩🏼‍🏫 استاد #سمانه_پرواز\n📚 دروس #علوم_پایه  \n✅ نکات مثبت :\n۱- خوش اخلاق\n۲- تدريس خيلي خوب و قابل فهمه \n۳- تمرین حل میکنن\n۴- نمره دادنشون خوبه\n۵- به حرف دانشجو گوش میده اگه مشکلی برات چه از نظر زمان واسه خوندن امتحان و چه از نظر غیبت کردن واسه کار و‌... داشته باشی باهات راه میاد\n۶- خیلی اهل انداختن دانشجو نیست اگر افتادی بدون درس نخوندی🤔\n۷- وقت استراحت میدن\n۸- اگه دوست داشته باشین میان ترم میگیرن اگه نه که نمیگیرن\n\n❌ نکات منفی :\n۱- بعضی وقتا تیکه میندازن😬\n\nپ.ن.۱: كل تايم كلاس نگه ميدارن\n\nپ.ن.۲: از كتاب چندتا تمرين مشخص ميكنن بايد تحويل داد\n\n@WTIAU_ostad 🤓');
